import React, { useState, useEffect } from "react";
import { executeDevices } from "../services/api"; // Use the executeDevices function from API service
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Check if the token exists in localStorage, otherwise redirect to login
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if token is missing
    }
  }, [navigate]);

  const handleFileUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please upload a valid YAML file.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await executeDevices(file); // Use the executeDevices function
      if (response.success) {
        setResults(response.data.results); // Set results from backend response
      } else {
        setError(response.message);
      }
    } catch (error) {
      console.error("Error uploading file:", error);
      setError("Failed to execute commands. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Device Management</h2>
      <form onSubmit={handleFileUpload}>
        <label>Upload YAML File:</label>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit" disabled={loading}>
          {loading ? "Executing..." : "Execute"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <h3>Execution Results:</h3>
      {results.length === 0 ? (
        <p>No results to display yet.</p>
      ) : (
        <ul>
          {results.map((result, index) => (
            <li key={index}>
              <strong>Device:</strong> {result.device}
              <ul>
                {result.results.map((action, idx) => (
                  <li key={idx}>
                    <strong>Action:</strong> {action.action} <br />
                    <strong>Service:</strong> {action.service} <br />
                    <strong>Status:</strong> {action.result.status} <br />
                    <strong>Output:</strong> <pre>{action.result.output || "No output"}</pre>
                    <strong>Error:</strong> <pre>{action.result.error || "No error"}</pre>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Dashboard;
