import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import AboutUs from './pages/AboutUs';
import ContactUs from './pages/ContactUs';

const App = () => {
    const [theme, setTheme] = useState('light');

    const toggleTheme = () => {
        const newTheme = theme === 'light' ? 'dark' : 'light';
        setTheme(newTheme);
        document.documentElement.setAttribute('data-theme', newTheme);
    };

    return (
        <div>
            <Router>
                <nav style={{ padding: '10px', backgroundColor: '#f4f4f9', textAlign: 'center' }}>
                    <Link to="/" style={{ margin: '0 10px' }}>Home</Link>
                    <Link to="/about" style={{ margin: '0 10px' }}>About Us</Link>
                    <Link to="/contact" style={{ margin: '0 10px' }}>Contact Us</Link>
                </nav>
                <Switch>
                    <Route path="/about" component={AboutUs} />
                    <Route path="/contact" component={ContactUs} />
                    <Route path="/" exact>
                        <div style={{ padding: '20px', textAlign: 'center' }}>
                            <h1>Welcome to RCMS</h1>
                            <p>Your go-to solution for remote control management.</p>
                        </div>
                    </Route>
                </Switch>
            </Router>
            <button id="theme-toggle" onClick={toggleTheme}>ðŸŒ“</button>
        </div>
    );
};

export default App;
