import React, { useState, useEffect } from "react";
import { executeDevices } from "../services/api"; // API function to handle file upload
import { useNavigate } from "react-router-dom";
import "./Dashboard.css";

const Dashboard = () => {
    const [file, setFile] = useState(null);
    const [results, setResults] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // Redirect to login if token is not found in localStorage
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/login"); // Redirect to login page
        }
    }, [navigate]);

    const handleFileUpload = async (e) => {
        e.preventDefault();

        if (!file) {
            setError("Please upload a valid YAML file.");
            return;
        }

        setLoading(true);
        setError(null);

        try {
            const response = await executeDevices(file); // Call the API function
            if (response.success) {
                setResults(response.data.results); // Store results in state
                // Navigate to the Results page with the result data
                navigate("/results", { state: { result: response.data.results } });
            } else {
                setError(response.message);
            }
        } catch (error) {
            console.error("Error uploading file:", error);
            setError("Failed to execute commands. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h2>Device Management</h2>
                <p>Upload a YAML file to execute commands on devices.</p>
            </div>

            <form onSubmit={handleFileUpload} className="file-upload-form">
                <div className="file-input-group">
                    <label htmlFor="file-upload" className="file-label">
                        Select YAML File
                    </label>
                    <input
                        id="file-upload"
                        type="file"
                        onChange={(e) => setFile(e.target.files[0])}
                        className="file-input"
                    />
                </div>
                <button type="submit" disabled={loading} className="submit-button">
                    {loading ? "Executing..." : "Execute"}
                </button>
            </form>

            {error && <p className="error-message">{error}</p>}

            {/* Display results below dynamically (if staying on the same page) */}
            {results && (
                <div className="results-section">
                    <h3>Execution Results:</h3>
                    <pre className="results-display">
                        {JSON.stringify(results, null, 2)}
                    </pre>
                </div>
            )}
        </div>
    );
};

export default Dashboard;
