import React, { useState, useEffect } from "react";
import axios from "axios"; // Ensure this points to the correct axios instance
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [file, setFile] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Check if the token exists in localStorage, otherwise redirect to login
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirect to login if token is missing
    }
  }, [navigate]);

  const handleFileUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setError("Please upload a valid YAML file.");
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("/devices/execute", formData, {
        headers: {
          Authorization: `Bearer ${token}`, // Attach JWT token for authorization
        },
      });
      setResults(response.data.results); // Set results from backend response
    } catch (error) {
      console.error("Error uploading file:", error);
      setError("Failed to execute commands. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Device Management</h2>
      <form onSubmit={handleFileUpload}>
        <label>Upload YAML File:</label>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit" disabled={loading}>
          {loading ? "Executing..." : "Execute"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <h3>Execution Results:</h3>
      {results.length === 0 ? (
        <p>No results to display yet.</p>
      ) : (
        <ul>
          {results.map((result, index) => (
            <li key={index}>
              {result.device}: {JSON.stringify(result.result)}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Dashboard;
