import axios from 'axios';

// Create an Axios instance for API calls
const api = axios.create({
  baseURL: 'http://127.0.0.1:5000', // Backend URL
  timeout: 5000, // Timeout for requests
});

export const login = async (username, password) => {
  try {
    // Make the API request
    const response = await api.post('/auth/login', { username, password });

    // Check if the response contains the access token
    if (response && response.data && response.data.access_token) {
      const { access_token } = response.data;
      localStorage.setItem('token', access_token); // Store token locally
      return { success: true }; // Indicate success
    } else {
      // Handle unexpected server response structure
      return { success: false, message: 'Unexpected response format from server' };
    }
  } catch (error) {
    // Check if the error is due to a network issue or server response
    if (error.response) {
      // Server responded with a status other than 2xx
      const message =
        error.response.data?.error || 'Invalid username or password';
      return { success: false, message };
    } else if (error.request) {
      // Request was made but no response was received
      return { success: false, message: 'Server did not respond. Please try again.' };
    } else {
      // Other errors (e.g., in setting up the request)
      return { success: false, message: 'An unexpected error occurred' };
    }
  }
};
