import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:5000', // Backend URL
});

export const login = async (username, password) => {
  try {
    const response = await api.post('/auth/login', { username, password });
    const { access_token } = response.data; // Extract token
    localStorage.setItem('token', access_token); // Store token
    return { success: true };
  } catch (error) {
    console.error('Login failed:', error.response.data);
    return { success: false, message: error.response.data.error || 'Invalid credentials' };
  }
};
